mapper.insert:
INSERT INTO MESSAGE ( MSG_ID, MSG_SENDER, MSG_RECEIVER, MSG_CONTENT, MSG_SEP, MSG_REG_DT, MSG_READ_YN, MSG_USE_YN
, RSM_ID, MSG_NOTIFY
) VALUES ( MSG_SEQ.NEXTVAL, #{msg_sender}, #{msg_receiver}, #{msg_content}, #{msg_sep}, SYSDATE, 1, 1
, #{rsm_id}, #{msg_notify}
)

mapper.delete:
UPDATE MESSAGE
SET msg_use_yn = 0
WHERE MSG_ID   = #{msg_id}

UPDATE MESSAGE
SET msg_read_yn = 0
WHERE MSG_ID   = #{msg_id}
